import * as astro_integration_kit from 'astro-integration-kit';
import { Route } from '../types.js';
import * as sitemap from 'sitemap';
import '../options.js';
import 'astro/zod';
import 'astro';

declare const ROUTES_DIR = "routes";
declare const handleRouting: astro_integration_kit.HookUtility<"astro:config:setup", [options: {
    defaultLocale: string;
    locales: string[];
    strategy: "prefix" | "prefixExceptDefault";
    pages: {
        [k: string]: Record<string, string | undefined>;
    };
    localesDir: string;
    defaultNamespace: string;
    client: {
        translations: boolean;
        data: boolean;
        paths: boolean;
    };
    rootRedirect?: {
        status: 300 | 301 | 302 | 303 | 304 | 307 | 308;
        destination: string;
    } | undefined;
    sitemap?: {
        entryLimit: number;
        customPages?: string[] | undefined;
        changefreq?: sitemap.EnumChangefreq | undefined;
        lastmod?: Date | undefined;
        priority?: number | undefined;
    } | undefined;
}], {
    routes: Route[];
}>;

export { ROUTES_DIR, handleRouting };
