import * as astro_integration_kit from 'astro-integration-kit';
import { RouteData } from 'astro';

type ConfigContext = {
    route: string[];
    routeData: RouteData[];
    component: string;
};

type ConfigHandler<T> = (context: ConfigContext, value: T) => Promise<void> | void;
type PerRouteConfigOptions<T> = {
    importName: string;
    callbackHandler: ConfigHandler<T>;
};
declare const _default: astro_integration_kit.Plugin<"defineRouteConfig", {
    'astro:config:setup': {
        defineRouteConfig: <T = any>(options: PerRouteConfigOptions<T>) => void;
    };
}>;

export { _default as default };
